package javacorepractice.classassignment;

public class SwapNumberExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		int b = 20;
		
		//System.out.println(a);
		//System.out.println(b);
		
		a = a+b;    //a is 30
		b = a-b;    // b is 10
		a = a-b;    // a is 20
		
		System.out.println(a);
		System.out.println(b);
		
		
		 

	}

}
